import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import PaperListScreen from './screens/PaperListScreen';
import PDFViewerScreen from './screens/PDFViewerScreen';

const App = () => {
  console.log("inside APP");
  const [pdfUrl, setPdfUrl] = useState(null); // State to store the PDF URL
  const [pdfTitle, setPdfTitle] = useState(''); // State to store the PDF title

  const handlePaperSelect = (url, title) => {
    setPdfUrl(url);  // Set the selected PDF URL
    setPdfTitle(title); // Set the selected PDF title
  };

  return (
    <Router>
      <div style={styles.container}>
        <div style={styles.paperList}>
          <PaperListScreen onPaperSelect={handlePaperSelect} />
        </div>
      </div>
    </Router>
  );
};

const styles = {
  container: {
    display: 'flex',
    height: '100vh', // Full height of the viewport
  },
  paperList: {
    flex: 1, // Take up remaining space
    overflowY: 'auto', // Enable scrolling if content overflows
    borderRight: '1px solid #ccc', // Optional: add a border between sections
    padding: '10px', // Optional: padding for better spacing
  },
  pdfViewer: {
    //width: '600px', // Fixed width for PDF viewer (adjust as needed)
    padding: '10px', // Optional: padding for better spacing
  },
};

export default App;
