import React, { useState, useEffect } from 'react';

const CardView = ({ papers, selectedPaper, handlePaperSelection }) => {
  const [summaries, setSummaries] = useState({});
  const [error, setError] = useState(null);

  const fetchSummary = async (paperId, textToSummarize) => {
    setError(null);
    try {
      const response = await fetch(`http://localhost:8000/summary`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ text: textToSummarize }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || `HTTP error! status: ${response.status}`);
      }

      setSummaries(prev => ({ ...prev, [paperId]: data.data.summary }));
    } catch (err) {
      console.error("Error fetching summary:", err);
      setError(err.message);
    }
  };

  useEffect(() => {
    if (selectedPaper && selectedPaper.summary && !summaries[selectedPaper.id]) {
      fetchSummary(selectedPaper.id, selectedPaper.summary);
    }
  }, [selectedPaper]);

  return (
    <div style={styles.paperList}>
      {papers.map((item) => (
        <div key={item.id} style={styles.card}>
          <button
            onClick={() => handlePaperSelection(item)}
            style={{
              ...styles.title,
              fontWeight: selectedPaper && selectedPaper.id === item.id ? 'bold' : 'normal',
            }}
          >
            {item.title}
          </button>
          <p>Authors: {item.authors.join(', ')}</p>
          <p>{summaries[item.id] || item.summary}</p>
          <p>Published: {item.published}</p>
        </div>
      ))}
      {error && <p style={styles.error}>{error}</p>}
    </div>
  );
};

const styles = {
  // ... (keep existing styles)
  error: {
    color: 'red',
    fontWeight: 'bold',
    marginTop: '10px',
  },
};

export default CardView;
